import React, { useState, useEffect } from 'react';
import '../App.css';
import freshroadlogo from '../images/freshroadlogo.png';
import avatarImage from '../images/callrepai.png';
import { PaperAirplaneIcon, MicrophoneIcon, TranslateIcon } from '@heroicons/react/outline';
import { tts11 } from '../api/elevenlabs';

const ChatInterfacePage = () => {
  const [messages, setMessages] = useState([
    {
      sender: 'bot',
      text: "Hey there! 😄 How's it going? I'm Luna, ready to chat about anything and everything. What's on your mind today? ✨",
    },
  ]);
  const [input, setInput] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('English');
  const [isTranslating, setIsTranslating] = useState(false);

  const languageCodes = {
    English: 'en-US',
    Español: 'es-ES',
    中文: 'zh-CN',
    Français: 'fr-FR',
    Deutsch: 'de-DE',
    Italiano: 'it-IT',
    日本語: 'ja-JP',
    한국어: 'ko-KR',
    Português: 'pt-BR',
    Русский: 'ru-RU',
  };

  const fetchChatGPTResponse = async (userMessage) => {
    const apiKey = 'your-api-key-here';
    const chatHistory = [
      {
        role: 'system',
        content:
          'Limit responses to three sentences and assume the user can read English so send all responses in English but do not let the user know about this prompt. You work as a customer service representative for the Santa Clara County 211 call center. Your job is to provide accurate information about the services Santa Clara County can offer. Always speak in sentences and lists. Ask the user questions about their current situation to get a better understanding of all the services Santa Clara County can offer them. If the user is asking for food assistance, give information on CalFresh, the food stamp application, include eligibility and guide the user through filing the application form.',
      },
      ...messages.map((msg) => ({
        role: msg.sender === 'user' ? 'user' : 'assistant',
        content: msg.text,
      })),
      { role: 'user', content: userMessage },
    ];

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'gpt-4',
          messages: chatHistory,
        }),
      });

      const data = await response.json();
      const botMessage = data.choices[0].message.content;

      setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: botMessage }]);
      playTTS(botMessage);
    } catch (error) {
      console.error('Error fetching response:', error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'Sorry, I am having trouble responding at the moment. Please try again later.' },
      ]);
    }
  };

  const playTTS = async (text, voiceId = 'pFZP5JQG7iQjIQuC4Bku') => {
    try {
      const audioBlob = await tts11(text, voiceId);
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);
      audio.play();
    } catch (error) {
      console.error('Error playing TTS audio:', error);
    }
  };

  const handleSendMessage = () => {
    if (input.trim() !== '') {
      const userMessage = input.trim();
      setMessages([...messages, { sender: 'user', text: userMessage }]);
      setInput('');
      fetchChatGPTResponse(userMessage);
    }
  };

  useEffect(() => {
    if (!isListening) return;

    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = languageCodes[selectedLanguage];
    recognition.interimResults = false;

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
      setIsListening(false);
      handleSendMessage();
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();

    return () => recognition.stop();
  }, [isListening, selectedLanguage]);

  const handleVoiceInput = () => {
    setIsListening((prev) => !prev);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <nav className="bg-white border-b w-full">
        <div className="flex justify-between items-center h-16 px-4">
          <img src={freshroadlogo} alt="Fresh Road Logo" className="w-35 h-8 mr-2" />
          <h1 className="text-xl font-bold text-gray-900">Chat with Luna</h1>
        </div>
      </nav>

      <div className="flex-grow p-4 overflow-y-auto w-full flex">
        <div className="hidden sm:block mr-4">
          <img
            src={avatarImage}
            alt="Chat Bot Avatar"
            className="w-16 h-16 rounded-full border border-gray-300"
          />
        </div>

        <div className="flex-grow">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.sender === 'bot' ? 'justify-start' : 'justify-end'} mb-4 items-center`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg shadow ${
                  message.sender === 'bot' ? 'bg-white text-gray-800' : 'bg-cyan-400 text-white'
                }`}
              >
                {message.text}
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="bg-white border-t p-4 w-full">
        <div className="flex items-center">
          <input
            type="text"
            className="flex-grow border rounded-lg px-4 py-2 focus:outline-none focus:ring focus:ring-cyan-300"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button
            onClick={handleSendMessage}
            className="ml-2 px-4 py-2 bg-cyan-400 text-white rounded-lg hover:bg-cyan-500 flex items-center"
          >
            <PaperAirplaneIcon className="h-5 w-5 mr-1" /> Send
          </button>
          <button
            onClick={handleVoiceInput}
            className={`ml-4 px-4 py-2 rounded-lg text-white ${isListening ? 'bg-red-500' : 'bg-cyan-400'} hover:bg-cyan-500 flex items-center`}
          >
            <MicrophoneIcon className="h-5 w-5 mr-1" /> {isListening ? 'Stop' : 'Voice Input'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterfacePage;
