import React from 'react';
import { Link } from 'react-router-dom';
import '../App.css'; // Ensure correct path
import freshroadlogo from '../images/freshroadlogo.png';
import { Flag, BookOpen } from 'lucide-react';

const CivicsLearningPage = () => {
  const civicsSections = [
    {
      icon: <Flag className="w-8 h-8 text-cyan-400" />, 
      title: 'Principles of American Democracy',
      description: 'Learn the foundational principles of democracy in the U.S.',
      link: '/civics/principles-of-democracy'
    },
    {
      icon: <Flag className="w-8 h-8 text-cyan-400" />, 
      title: 'System of Government',
      description: 'Understand the structure and functions of the U.S. government.',
      link: '/civics/system-of-government'
    },
    {
      icon: <Flag className="w-8 h-8 text-cyan-400" />, 
      title: 'Rights and Responsibilities',
      description: 'Explore the rights and responsibilities of U.S. citizens.',
      link: '/civics/rights-and-responsibilities'
    },
    {
      icon: <Flag className="w-8 h-8 text-cyan-400" />, 
      title: 'American History',
      description: 'Dive into the key moments and figures in U.S. history.',
      link: '/civics/american-history'
    },
    {
      icon: <Flag className="w-8 h-8 text-cyan-400" />, 
      title: 'Integrated Civics',
      description: 'Learn about U.S. geography, symbols, and holidays.',
      link: '/civics/integrated-civics'
    }
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Navigation */}
      <nav className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <img 
                src={freshroadlogo} 
                alt="Fresh Road Logo" 
                className="w-35 h-8 mr-2" 
              />
            </div>

            <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
              <Link to="/" className="text-gray-900 inline-flex items-center px-1 pt-1">
                Home
              </Link>
              <Link to="/resources" className="text-gray-500 hover:text-gray-900 inline-flex items-center px-1 pt-1">
                Resources
              </Link>
              <Link to="/community" className="text-gray-500 hover:text-gray-900 inline-flex items-center px-1 pt-1">
                Community
              </Link>
            </div>

            <div className="flex items-center">
              <button className="text-white px-4 py-2 rounded-md hover:bg-blue-700 bg-cyan-400">
                Sign In
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <h2 className="text-4xl font-bold text-center mb-4">
          Civics Learning Sections
        </h2>
        <p className="text-xl text-gray-600 text-center mb-12">
          Explore different sections to enhance your understanding of U.S. Civics.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {civicsSections.map((section, index) => (
            <Link 
              to={section.link} 
              key={index} 
              className="flex flex-col items-center p-6 hover:bg-gray-50 rounded-xl cursor-pointer border border-gray-100 transition-all hover:shadow-md"
            >
              {section.icon}
              <h3 className="text-lg font-semibold text-gray-900 mt-4">{section.title}</h3>
              <p className="text-sm text-gray-600 text-center mt-2">{section.description}</p>
            </Link>
          ))}
        </div>
      </main>
    </div>
  );
};

export default CivicsLearningPage;
