import React, { useState } from 'react';
import '../App.css'; // Ensure correct path to your CSS file
import freshroadlogo from '../images/freshroadlogo.png';

const ChatInterfacePage = () => {
  const [messages, setMessages] = useState([
    {
      sender: 'bot',
      text: "Hey there! ðŸ˜„ How's it going? I'm Luna, ready to chat about anything and everything. What's on your mind today? âœ¨",
    },
  ]);
  const [input, setInput] = useState('');

  const handleSendMessage = () => {
    if (input.trim() !== '') {
      setMessages([...messages, { sender: 'user', text: input }]);
      setInput('');
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          {
            sender: 'bot',
            text: 'Thanks for sharing! How can I help further? ðŸ˜Š',
          },
        ]);
      }, 1000); // Simulated bot response delay
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* Header */}
      <nav className="bg-white border-b w-full max-w-md">
        <div className="flex justify-between items-center h-16 px-4">
          <img src={freshroadlogo} alt="Fresh Road Logo" className="w-35 h-8 mr-2" />
          <h1 className="text-xl font-bold text-gray-900">Chat with Luna</h1>
        </div>
      </nav>

      {/* Chat Body */}
      <div className="flex-grow p-4 overflow-y-auto w-full max-w-md">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${
              message.sender === 'bot' ? 'justify-start' : 'justify-end'
            } mb-4`}
          >
            <div
              className={`max-w-xs px-4 py-2 rounded-lg shadow ${
                message.sender === 'bot' ? 'bg-white text-gray-800' : 'bg-cyan-400 text-white'
              }`}
            >
              {message.text}
            </div>
          </div>
        ))}
      </div>

      {/* Chat Input */}
      <div className="bg-white border-t p-4 w-full max-w-md">
        <div className="flex items-center">
          <input
            type="text"
            className="flex-grow border rounded-lg px-4 py-2 focus:outline-none focus:ring focus:ring-cyan-300"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button
            onClick={handleSendMessage}
            className="ml-2 px-4 py-2 bg-cyan-400 text-white rounded-lg hover:bg-cyan-500"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterfacePage;
